thm frindi(setvar s0, class c0, class c1, class c2) {
  ⊢ ((c0 ⊆ c1 ∧ ∀(s0 ∈ c1,(Pred(c2, c1, s0)⊆ c0 → s0 ∈ c0)))→ c1 = c0)
  ⊣ c2[Fr]c1
  ⊣ c2[Se]c1
  ⊣ d(s0, c0)
  ⊣ d(s0, c1)
  ⊣ d(s0, c2)
} = {
  mpanl12((c0 ⊆ c1 ∧ ∀(s0 ∈ c1,(Pred(c2, c1, s0)⊆ c0 → s0 ∈ c0))), c1 = c0, c2[Fr]c1, c2[Se]c1)
  frind(s0, c2, c1, c0)
}
