thm bj-mp2c(wff w0, wff w1, wff w2) {
  |- w0
  -| w1
  -| wi(w1, w2)
  -| wi(w1, wi(w2, w0))
} = {
  mp2(w0, w1, w2)
  ax-mp(w2, w1)
}

