thm bj-dfbi4(wff w0, wff w1) {
  |- wb(wb(w0, w1), wo(wa(w0, w1), wn(wo(w0, w1))))
} = {
  bitri(wb(w0, w1), wo(wa(w0, w1), wn(wo(w0, w1))), wo(wa(w0, w1), wa(wn(w0), wn(w1))))
  dfbi3(w0, w1)
  orbi2i(wa(w0, w1), wa(wn(w0), wn(w1)), wn(wo(w0, w1)))
  pm4.56(w0, w1)
}

thm bj-dfbi5(wff w0, wff w1) {
  |- wb(wb(w0, w1), wi(wo(w0, w1), wa(w0, w1)))
} = {
  3bitr4i(wb(w0, w1), wi(wo(w0, w1), wa(w0, w1)), wo(wa(w0, w1), wn(wo(w0, w1))), wo(wn(wo(w0, w1)), wa(w0, w1)))
  orcom(wa(w0, w1), wn(wo(w0, w1)))
  bj-dfbi4(w0, w1)
  imor(wo(w0, w1), wa(w0, w1))
}

thm bj-dfbi6(wff w0, wff w1) {
  |- wb(wb(w0, w1), wb(wo(w0, w1), wa(w0, w1)))
} = {
  bitri(wb(w0, w1), wb(wo(w0, w1), wa(w0, w1)), wi(wo(w0, w1), wa(w0, w1)))
  bj-dfbi5(w0, w1)
  impbii(wi(wo(w0, w1), wa(w0, w1)), wb(wo(w0, w1), wa(w0, w1)))
  impbid1(wi(wo(w0, w1), wa(w0, w1)), wo(w0, w1), wa(w0, w1))
  id(wi(wo(w0, w1), wa(w0, w1)))
  animorr(w0, w1, w0)
  biimp(wo(w0, w1), wa(w0, w1))
}

thm bj-bijust0ALT(wff w0) {
