thm bj-consensus(wff w0, wff w1, wff w2) {
  |- wb(wo(wif(w0, w1, w2), wa(w1, w2)), wif(w0, w1, w2))
} = {
  impbii(wo(wif(w0, w1, w2), wa(w1, w2)), wif(w0, w1, w2))
  bj-jaoi2(wif(w0, w1, w2), wa(w1, w2))
  anifp(w1, w2, w0)
  orc(wif(w0, w1, w2), wa(w1, w2))
}

