thm bj-consensus(wff w0, wff w1, wff w2) {
  ⊢ ((if(w0, w1, w2)∨(w1 ∧ w2))↔ if(w0, w1, w2))
} = {
  impbii((if(w0, w1, w2)∨(w1 ∧ w2)), if(w0, w1, w2))
  bj-jaoi2(if(w0, w1, w2), (w1 ∧ w2))
  anifp(w1, w2, w0)
  orc(if(w0, w1, w2), (w1 ∧ w2))
}

