thm bj-consensusALT(wff w0, wff w1, wff w2) {
  |- wb(wo(wif(w0, w1, w2), wa(w1, w2)), wif(w0, w1, w2))
} = {
  bitr4i(wo(wif(w0, w1, w2), wa(w1, w2)), wif(w0, w1, w2), wo(wa(w1, w2), wif(w0, w1, w2)))
  orcom(wif(w0, w1, w2), wa(w1, w2))
  mpbi(wb(wif(w0, w1, w2), wo(wa(w1, w2), wif(w0, w1, w2))), wi(wa(w1, w2), wif(w0, w1, w2)))
  anifp(w1, w2, w0)
  pm4.72(wa(w1, w2), wif(w0, w1, w2))
}

