thm bj-df-ifc(setvar s0, class c0, class c1, wff w0) {
  |- wceq(cif(c0, c1, w0), cab(s0, wif(w0, wcel(cv(s0), c0), wcel(cv(s0), c1))))
  -| diffsc(s0, c0)
  -| diffsc(s0, c1)
  -| diffsw(s0, w0)
} = {
  eqtri(cif(c0, c1, w0), cab(s0, wif(w0, wcel(cv(s0), c0), wcel(cv(s0), c1))), cab(s0, wo(wa(w0, wcel(cv(s0), c0)), wa(wn(w0), wcel(cv(s0), c1)))))
  bj-dfifc2(s0, c0, c1, w0)
  abbii(s0, wo(wa(w0, wcel(cv(s0), c0)), wa(wn(w0), wcel(cv(s0), c1))), wif(w0, wcel(cv(s0), c0), wcel(cv(s0), c1)))
  bicomi(wo(wa(w0, wcel(cv(s0), c0)), wa(wn(w0), wcel(cv(s0), c1))), wif(w0, wcel(cv(s0), c0), wcel(cv(s0), c1)))
  df-ifp(w0, wcel(cv(s0), c0), wcel(cv(s0), c1))
}

blocks.pretty:
thm bj-df-ifc(setvar s0, class c0, class c1, wff w0) {
  ⊢ if(w0, c0, c1)= { s0 | if(w0, s0 ∈ c0, s0 ∈ c1)}
  ⊣ d(s0, c0)
  ⊣ d(s0, c1)
  ⊣ d(s0, w0)
} = {
  eqtri(if(w0, c0, c1), { s0 | if(w0, s0 ∈ c0, s0 ∈ c1)}, { s0 |((w0 ∧ s0 ∈ c0)∨(¬ w0 ∧ s0 ∈ c1))})
  bj-dfifc2(s0, c0, c1, w0)
  abbii(s0, ((w0 ∧ s0 ∈ c0)∨(¬ w0 ∧ s0 ∈ c1)), if(w0, s0 ∈ c0, s0 ∈ c1))
  bicomi(((w0 ∧ s0 ∈ c0)∨(¬ w0 ∧ s0 ∈ c1)), if(w0, s0 ∈ c0, s0 ∈ c1))
  df-ifp(w0, s0 ∈ c0, s0 ∈ c1)
}

