thm bj-ififc(setvar s0, class c0, class c1, wff w0) {
  |- wb(wcel(cv(s0), cif(c0, c1, w0)), wif(w0, wcel(cv(s0), c0), wcel(cv(s0), c1)))
  -| diffsc(s0, c0)
  -| diffsc(s0, c1)
  -| diffsw(s0, w0)
} = {
  abeq2i(s0, cif(c0, c1, w0), wif(w0, wcel(cv(s0), c0), wcel(cv(s0), c1)))
  bj-df-ifc(s0, c0, c1, w0)
}

