thm bj-nfcsym(setvar s0, setvar s1) {
  |- wb(wnfc(s0, cv(s1)), wnfc(s1, cv(s0)))
} = {
  pm2.61i(wb(wnfc(s0, cv(s1)), wnfc(s1, cv(s0))), wal(s0, wceq(cv(s0), cv(s1))))
  drnfc1(s0, s1, cv(s1), cv(s0))
  equcomd(s1, s0, wal(s0, wceq(cv(s0), cv(s1))))
  sp(s0, wceq(cv(s0), cv(s1)))
  2thd(wn(wal(s0, wceq(cv(s0), cv(s1)))), wnfc(s0, cv(s1)), wnfc(s1, cv(s0)))
  nfcvf(s0, s1)
  nfcvf2(s0, s1)
}

