thm bj-nfcsym(setvar s0, setvar s1) {
  ⊢ (F/(s0, s1)↔ F/(s1, s0))
} = {
  pm2.61i((F/(s0, s1)↔ F/(s1, s0)), ∀(s0, s0 = s1))
  drnfc1(s0, s1, s1, s0)
  equcomd(s1, s0, ∀(s0, s0 = s1))
  sp(s0, s0 = s1)
  2thd(¬ ∀(s0, s0 = s1), F/(s0, s1), F/(s1, s0))
  nfcvf(s0, s1)
  nfcvf2(s0, s1)
}
