blocks.origin:
axiom df-bj-rrvec(setvar s0) {
  |- wceq(crrvec, crab(s0, clvec, wceq(cfv(csca, cv(s0)), crefld)))
}

blocks.pretty:
axiom df-bj-rrvec(setvar s0) {
  ⊢ RRVec = { s0 ∈ LVec |(Scalar ` s0)= RRfld }
}

blocks.origin:
thm bj-rrvecssvec() {
  |- wss(crrvec, clvec)
} = {
  eqsstri(crrvec, clvec, crab(hs0, clvec, wceq(cfv(csca, cv(hs0)), crefld)))
  df-bj-rrvec(hs0)
  ssrab2(hs0, clvec, wceq(cfv(csca, cv(hs0)), crefld))
  diff.hs0.c(clvec)
}

blocks.pretty:
thm bj-rrvecssvec() {
  ⊢ RRVec ⊆ LVec
} = {
  eqsstri(crrvec, clvec, { hs0 ∈ LVec |(Scalar ` hs0)= RRfld })
  df-bj-rrvec(hs0)
  ssrab2(hs0, clvec, (Scalar ` hs0)= RRfld)
  diff.hs0.c(clvec)
}

