thm bj-subcom(class c0, class c1, wff w0) {
  ⊢ (w0 →((c0[×]c1)[-](c1[×]c0))= 0)
  ⊣ (w0 → c0 ∈ ℂ)
  ⊣ (w0 → c1 ∈ ℂ)
} = {
  subeq0bd((c0[×]c1), (c1[×]c0), w0)
  mulcld(c0, c1, w0)
  mulcomd(c0, c1, w0)
}

