thm bj-lineqi(class c0, class c1, class c2, class c3, wff w0) {
  |- wi(w0, wceq(c0, co(co(c1, cmin, c2), cdiv, c3)))
  -| wi(w0, wcel(c3, cc))
  -| wi(w0, wcel(c2, cc))
  -| wi(w0, wcel(c0, cc))
  -| wi(w0, wcel(c1, cc))
  -| wi(w0, wne(c3, nat0))
  -| wi(w0, wceq(co(co(c3, cmul, c0), caddc, c2), c1))
} = {
  mpbid(w0, wceq(c0, co(co(c1, cmin, c2), cdiv, c3)), wceq(co(co(c3, cmul, c0), caddc, c2), c1))
  lineq(c3, c0, c2, c1, w0)
}
