axiom df-ass(setvar s0, setvar s1, setvar s2, setvar s3) {
  ⊢ Ass = { s0 | ∀(s1 ∈ dom(dom(s0)), ∀(s2 ∈ dom(dom(s0)), ∀(s3 ∈ dom(dom(s0)),((s1[s0]s2)[s0]s3)=(s1[s0](s2[s0]s3)))))}
  ⊣ d(s0, s1)
  ⊣ d(s0, s2)
  ⊣ d(s0, s3)
  ⊣ d(s1, s2)
  ⊣ d(s1, s3)
  ⊣ d(s2, s3)
}

