axiom df-com2(setvar s0, setvar s1, setvar s2, setvar s3) {
  ⊢ Com2 = { < s0, s1 > | ∀(s2 ∈ ran(s0), ∀(s3 ∈ ran(s0),(s2[s1]s3)=(s3[s1]s2)))}
  ⊣ d(s2, s3)
  ⊣ d(s0, s2)
  ⊣ d(s1, s2)
  ⊣ d(s0, s3)
  ⊣ d(s1, s3)
  ⊣ d(s0, s1)
}

