  |- wi(wa(wcel(c0, chlt), wcel(c1, c2)), wb(wcel(c3, cfv(c4, c1)), wceq(c3, c1)))
  -| wceq(c2, cfv(catm, c0))
  -| wceq(c4, cfv(cpmap, c0))
} = {
  bitrd(wa(wcel(c0, chlt), wcel(c1, c2)), wcel(c3, cfv(c4, c1)), wceq(c3, c1), wcel(c3, csn(c1)))
  eleq2d(c3, cfv(c4, c1), csn(c1), wa(wcel(c0, chlt), wcel(c1, c2)))
  pmapat(c0, c1, c2, c4)
  adantl(wcel(c0, chlt), wcel(c1, c2), wb(wcel(c3, csn(c1)), wceq(c3, c1)))
  elsn2g(c1, c2, c3)
