  ‚ä¢ ((c0 ‚àà HL ‚àß c1 ‚àà c2)‚Üí(c3 ‚àà(c4 ` c1)‚Üî c3 = c1))
  ‚ä£ c2 =(Atoms ` c0)
  ‚ä£ c4 =(pmap ` c0)
} = {
  bitrd((c0 ‚àà HL ‚àß c1 ‚àà c2), c3 ‚àà(c4 ` c1), c3 = c1, c3 ‚àà { c1 })
  eleq2d(c3, (c4 ` c1), { c1 }, (c0 ‚àà HL ‚àß c1 ‚àà c2))
  pmapat(c0, c1, c2, c4)
  adantl(c0 ‚àà HL, c1 ‚àà c2, (c3 ‚àà { c1 } ‚Üî c3 = c1))
