thm mptfcl(setvar s0, class c0, class c1, class c2) {
  ⊢ (((s0 ∈ c0 ↦ c1): c0 → c2)→(s0 ∈ c0 → c1 ∈ c2))
  ⊣ d(s0, c0)
  ⊣ d(s0, c2)
} = {
  sylbir(((s0 ∈ c0 ↦ c1): c0 → c2), (s0 ∈ c0 → c1 ∈ c2), ∀(s0 ∈ c0, c1 ∈ c2))
  fmpt(s0, c0, c1, c2, (s0 ∈ c0 ↦ c1))
  eqid((s0 ∈ c0 ↦ c1))
  rsp(s0, c0, c1 ∈ c2)
}
