thm dvdsabsmod0(class c0, class c1) {
  ⊢ ((c0 ∈ ℤ ∧ c1 ∈ ℤ ∧ c0 ≠ 0)→(c0[||]c1 ↔(c1[mod](abs ` c0))= 0))
} = {
  3impa(c0 ∈ ℤ, c1 ∈ ℤ, c0 ≠ 0, (c0[||]c1 ↔(c1[mod](abs ` c0))= 0))
  an32s(c0 ∈ ℤ, c1 ∈ ℤ, c0 ≠ 0, (c0[||]c1 ↔(c1[mod](abs ` c0))= 0))
  bitrd(((c0 ∈ ℤ ∧ c0 ≠ 0)∧ c1 ∈ ℤ), c0[||]c1, (c1[mod](abs ` c0))= 0, (abs ` c0)[||]c1)
  adantlr(c0 ∈ ℤ, c0 ≠ 0, c1 ∈ ℤ, (c0[||]c1 ↔(abs ` c0)[||]c1))
  absdvdsb(c0, c1)
  sylan((c0 ∈ ℤ ∧ c0 ≠ 0), c1 ∈ ℤ, ((abs ` c0)[||]c1 ↔(c1[mod](abs ` c0))= 0), (abs ` c0)∈ ℕ)
  nnabscl(c0)
  dvdsval3((abs ` c0), c1)
