axiom df-toplnd(setvar s0, setvar s1, setvar s2) {
  |- wceq(ctoplnd, crab(s0, ctop, wral(s1, cpw(cv(s0)), wi(wceq(cuni(cv(s0)), cuni(cv(s1))), wrex(s2, cpw(cv(s0)), wa(wbr(cv(s2), cdom, com), wceq(cuni(cv(s0)), cuni(cv(s2)))))))))
  -| diffss(s0, s1)
  -| diffss(s0, s2)
  -| diffss(s1, s2)
}

blocks.pretty:
axiom df-toplnd(setvar s0, setvar s1, setvar s2) {
  ⊢ TopLnd = { s0 ∈ Top | ∀(s1 ∈ P(s0),(⋃ s0 = ⋃ s1 → ∃(s2 ∈ P(s0),(s2[≼]_om ∧ ⋃ s0 = ⋃ s2))))}
  ⊣ d(s0, s1)
  ⊣ d(s0, s2)
  ⊣ d(s1, s2)
}
