thm gneispacefun(setvar s0, setvar s1, setvar s2, setvar s3, class c0, class c1) {
  ⊢ (c0 ∈ c1 → Fun(c0))
  ⊣ c1 = { s0 |((s0 : dom(s0)→(P((P(dom(s0))\ { ∅ }))\ { ∅ }))∧ ∀(s1 ∈ dom(s0), ∀(s2 ∈(s0 ` s1),(s1 ∈ s2 ∧ ∀(s3 ∈ P(dom(s0)),(s2 ⊆ s3 → s3 ∈(s0 ` s1)))))))}
  ⊣ d(s0, s1)
  ⊣ d(s0, s2)
  ⊣ d(s0, s3)
  ⊣ d(s1, s2)
  ⊣ d(s0, c0)
  ⊣ d(s1, c0)
  ⊣ d(s2, c0)
  ⊣ d(s3, c0)
} = {
  ffund(c0, dom(c0), (P((P(dom(c0))\ { ∅ }))\ { ∅ }), c0 ∈ c1)
  gneispacef(s0, s1, s2, s3, c0, c1)
}
