thm tfis2f(setvar s0, setvar s1, wff w0, wff w1) {
  ⊢ (s0 ∈ On → w0)
  ⊣ F/(s0, w1)
  ⊣ (s0 = s1 →(w0 ↔ w1))
  ⊣ (s0 ∈ On →(∀(s1 ∈ s0, w1)→ w0))
  ⊣ d(s0, s1)
  ⊣ d(s1, w0)
} = {
  tfis(s0, s1, w0)
  syl5bi(s0 ∈ On, ∀(s1 ∈ s0,[s1 / s0]w0), w0, ∀(s1 ∈ s0, w1))
  ralbii(s1, s0, [s1 / s0]w0, w1)
  sbie(s1, s0, w0, w1)
}

