thm nvelim(class c0, class c1) {
  ⊢ (c0 = 𝕌 → ¬ c0 ∈ c1)
} = {
  mtbii(c0 = 𝕌, c0 ∈ c1, 𝕌 ∈ c1)
  nvel(c1)
  eqcoms(c0, universe, (𝕌 ∈ c1 ↔ c0 ∈ c1))
  eleq1(universe, c0, c1)
}
