thm nelbrim(class c0, class c1) {
  |- wi(wbr(c0, cnelbr, c1), wn(wcel(c0, c1)))
} = {
  mpcom(wbr(c0, cnelbr, c1), wn(wcel(c0, c1)), wa(wcel(c0, universe), wcel(c1, universe)))
  brrelex12i(c0, cnelbr, c1)
  relopabi(hs0, hs1, cnelbr, wn(wcel(cv(hs0), cv(hs1))))
  df-nelbr(hs0, hs1)
  biimpd(wa(wcel(c0, universe), wcel(c1, universe)), wbr(c0, cnelbr, c1), wn(wcel(c0, c1)))
  nelbr(c0, universe, c1, universe)
  diff.hs0.s(hs1)
}

