thm nelbrim(class c0, class c1) {
  ⊢ (c0[e/]c1 → ¬ c0 ∈ c1)
} = {
  mpcom(c0[e/]c1, ¬ c0 ∈ c1, (c0 ∈ 𝕌 ∧ c1 ∈ 𝕌))
  brrelex12i(c0, cnelbr, c1)
  relopabi(hs0, hs1, cnelbr, ¬ hs0 ∈ hs1)
  df-nelbr(hs0, hs1)
  biimpd((c0 ∈ 𝕌 ∧ c1 ∈ 𝕌), c0[e/]c1, ¬ c0 ∈ c1)
  nelbr(c0, universe, c1, universe)
  diff.hs0.s(hs1)
}

