thm leltletr(class c0, class c1, class c2) {
  ⊢ ((c0 ∈ ℝ ∧ c1 ∈ ℝ ∧ c2 ∈ ℝ)→((c0[≤]c1 ∧ c1[<]c2)→ c0[≤]c2))
} = {
  sylsyld((c0 ∈ ℝ ∧ c1 ∈ ℝ ∧ c2 ∈ ℝ), (c0[≤]c1 ∧ c1[<]c2), c0[≤]c2, (c0 ∈ ℝ ∧ c2 ∈ ℝ), c0[<]c2)
  3simpb(c0 ∈ ℝ, c1 ∈ ℝ, c2 ∈ ℝ)
  lelttr(c0, c1, c2)
  ltle(c0, c2)
}
