thm iseven5(class c0) {
  ⊢ (c0 ∈ Even ↔(c0 ∈ ℤ ∧(2[gcd]c0)= 2))
} = {
  bitri(c0 ∈ Even, (c0 ∈ ℤ ∧(2[gcd]c0)= 2), (c0 ∈ ℤ ∧ 2[||]c0))
  iseven2(c0)
  pm5.32i(c0 ∈ ℤ, 2[||]c0, (2[gcd]c0)= 2)
  bicomd(c0 ∈ ℤ, 2[||]c0, (2[gcd]c0)= 2)
  mpan(c0 ∈ ℤ, ((2[gcd]c0)= 2 ↔ 2[||]c0), 2 ∈ ℕ)
  2nn()
  gcdzeq(nat2, c0)
}

