thm isodd7(class c0) {
  ⊢ (c0 ∈ Odd ↔(c0 ∈ ℤ ∧(2[gcd]c0)= 1))
} = {
  bitri(c0 ∈ Odd, (c0 ∈ ℤ ∧(2[gcd]c0)= 1), (c0 ∈ ℤ ∧ ¬ 2[||]c0))
  isodd3(c0)
  pm5.32i(c0 ∈ ℤ, ¬ 2[||]c0, (2[gcd]c0)= 1)
  mpan(c0 ∈ ℤ, (¬ 2[||]c0 ↔(2[gcd]c0)= 1), 2 ∈ Prime)
  2prm()
  coprm(nat2, c0)
}
