axiom df-unc(setvar s0, setvar s1, setvar s2, class c0) {
  ⊢ uncurry(c0)= { < < s0, s1 >, s2 > | s1[(c0 ` s0)]s2 }
  ⊣ d(s0, s1)
  ⊣ d(s0, s2)
  ⊣ d(s1, s2)
  ⊣ d(s0, c0)
  ⊣ d(s1, c0)
  ⊣ d(s2, c0)
}
