thm submgmss(class c0, class c1, class c2) {
  |- wi(wcel(c0, cfv(csubmgm, c1)), wss(c0, c2))
  -| wceq(c2, cfv(cbs, c1))
} = {
  simpld(wcel(c0, cfv(csubmgm, c1)), wss(c0, c2), wcel(co(c1, cress, c0), cmgm))
  ibi(wcel(c0, cfv(csubmgm, c1)), wa(wss(c0, c2), wcel(co(c1, cress, c0), cmgm)))
  syl(wcel(c0, cfv(csubmgm, c1)), wb(wcel(c0, cfv(csubmgm, c1)), wa(wss(c0, c2), wcel(co(c1, cress, c0), cmgm))), wcel(c1, cmgm))
  submgmrcl(c0, c1)
  issubmgm2(c1, c0, c2, co(c1, cress, c0))
  eqid(co(c1, cress, c0))
}

