} = {
  simpld(c0 ∈(SubMgm ` c1), c0 ⊆ c2, (c1[|`s]c0)∈ Mgm)
  ibi(c0 ∈(SubMgm ` c1), (c0 ⊆ c2 ∧(c1[|`s]c0)∈ Mgm))
  syl(c0 ∈(SubMgm ` c1), (c0 ∈(SubMgm ` c1)↔(c0 ⊆ c2 ∧(c1[|`s]c0)∈ Mgm)), c1 ∈ Mgm)
  submgmrcl(c0, c1)
  issubmgm2(c1, c0, c2, (c1[|`s]c0))
  eqid((c1[|`s]c0))
}

