thm mapsnop(class c0, class c1, class c2, class c3, class c4, class c5) {
  ⊢ ((c0 ∈ c1 ∧ c2 ∈ c3 ∧ c3 ∈ c4)→ c5 ∈(c3[↦]{ c0 }))
  ⊣ c5 = { < c0, c2 > }
} = {
  mpbird((c0 ∈ c1 ∧ c2 ∈ c3 ∧ c3 ∈ c4), c5 ∈(c3[↦]{ c0 }), (c5 : { c0 } → c3))
  fssd(c5, { c0 }, c3, { c2 }, (c0 ∈ c1 ∧ c2 ∈ c3 ∧ c3 ∈ c4))
  mpbiri((c0 ∈ c1 ∧ c2 ∈ c3 ∧ c3 ∈ c4), (c5 : { c0 } → { c2 }), c5 = { < c0, c2 > })
  3adant3(c0 ∈ c1, c2 ∈ c3, c3 ∈ c4, ((c5 : { c0 } → { c2 })↔ c5 = { < c0, c2 > }))
  fsng(c0, c1, c2, c3, c5)
  3ad2ant2(c0 ∈ c1, c2 ∈ c3, c3 ∈ c4, { c2 } ⊆ c3)
  snssi(c2, c3)
  sylancl((c0 ∈ c1 ∧ c2 ∈ c3 ∧ c3 ∈ c4), (c5 ∈(c3[↦]{ c0 })↔(c5 : { c0 } → c3)), c3 ∈ c4, { c0 } ∈ 𝕌)
  simp3(c0 ∈ c1, c2 ∈ c3, c3 ∈ c4)
  snex(c0)
  elmapg(c3, c4, { c0 }, universe, c5)
}
