thm sbidd(setvar s0, wff w0, wff w1) {
  |- wi(w0, w1)
  -| wi(w0, wsb(s0, s0, w1))
} = {
  sylib(w0, w1, wsb(s0, s0, w1))
  sbid(s0, w1)
}

