thm sbidd(setvar s0, wff w0, wff w1) {
  ⊢ (w0 → w1)
  ⊣ (w0 →[s0 / s0]w1)
} = {
  sylib(w0, w1, [s0 / s0]w1)
  sbid(s0, w1)
}

