thm ifnmfalse(class c0, class c1, class c2, class c3) {
  |- wi(wnel(c0, c1), wceq(cif(c2, c3, wcel(c0, c1)), c3))
} = {
  sylbi(wnel(c0, c1), wceq(cif(c2, c3, wcel(c0, c1)), c3), wn(wcel(c0, c1)))
  df-nel(c0, c1)
  iffalse(c2, c3, wcel(c0, c1))
}

