thm ifnmfalse(class c0, class c1, class c2, class c3) {
  ⊢ (c0 ∉ c1 → if(c0 ∈ c1, c2, c3)= c3)
} = {
  sylbi(c0 ∉ c1, if(c0 ∈ c1, c2, c3)= c3, ¬ c0 ∈ c1)
  df-nel(c0, c1)
  iffalse(c2, c3, c0 ∈ c1)
}
