thm nic-dfim(wff w0, wff w1) {
  |- wnan(wnan(wnan(w0, wnan(w1, w1)), wi(w0, w1)), wnan(wnan(wnan(w0, wnan(w1, w1)), wnan(w0, wnan(w1, w1))), wnan(wi(w0, w1), wi(w0, w1))))
} = {
  mpbi(wnan(wnan(wnan(w0, wnan(w1, w1)), wi(w0, w1)), wnan(wnan(wnan(w0, wnan(w1, w1)), wnan(w0, wnan(w1, w1))), wnan(wi(w0, w1), wi(w0, w1)))), wb(wnan(w0, wnan(w1, w1)), wi(w0, w1)))
  bicomi(wnan(w0, wnan(w1, w1)), wi(w0, w1))
  nanim(w0, w1)
  nanbi(wnan(w0, wnan(w1, w1)), wi(w0, w1))
}

thm nic-dfneg(wff w0) {
  |- wnan(wnan(wnan(w0, w0), wn(w0)), wnan(wnan(wnan(w0, w0), wnan(w0, w0)), wnan(wn(w0), wn(w0))))
} = {
  mpbi(wnan(wnan(wnan(w0, w0), wn(w0)), wnan(wnan(wnan(w0, w0), wnan(w0, w0)), wnan(wn(w0), wn(w0)))), wb(wnan(w0, w0), wn(w0)))
  bicomi(wnan(w0, w0), wn(w0))
  nannot(w0)
  nanbi(wnan(w0, w0), wn(w0))
}

thm nic-mp(wff w0, wff w1, wff w2) {
  |- w0
  -| w1
  -| wnan(w1, wnan(w2, w0))
} = {
  ax-mp(w0, w1)
  simprd(w1, w0, w2)
  mpbi(wi(w1, wa(w2, w0)), wnan(w1, wnan(w2, w0)))
  nannan(w1, w2, w0)
}

