thm prsref(class c0, class c1, class c2, class c3) {
  |- wi(wa(wcel(c0, cproset), wcel(c1, c2)), wbr(c1, c3, c1))
  -| wceq(c2, cfv(cbs, c0))
  -| wceq(c3, cfv(cple, c0))
} = {
  simpld(wa(wcel(c0, cproset), wcel(c1, c2)), wbr(c1, c3, c1), wi(wa(wbr(c1, c3, c1), wbr(c1, c3, c1)), wbr(c1, c3, c1)))
  sylan2(wcel(c0, cproset), wcel(c1, c2), wa(wbr(c1, c3, c1), wi(wa(wbr(c1, c3, c1), wbr(c1, c3, c1)), wbr(c1, c3, c1))), w3a(wcel(c1, c2), wcel(c1, c2), wcel(c1, c2)))
  3jca(wcel(c1, c2), wcel(c1, c2), wcel(c1, c2), wcel(c1, c2))
  id(wcel(c1, c2))
  prslem(c0, c1, c2, c1, c1, c3)
}

