  odulat(c0, cfv(codu, c0))
  latjass(cfv(codu, c0), c1, c2, c3, c4, c5)
  odubas(c2, cfv(codu, c0), c0)
  odujoin(c5, cfv(codu, c0), c0)
  eqid(cfv(codu, c0))
