thm latmass(class c0, class c1, class c2, class c3, class c4, class c5) {
  ⊢ ((c0 ∈ Lat ∧(c1 ∈ c2 ∧ c3 ∈ c2 ∧ c4 ∈ c2))→((c1[c5]c3)[c5]c4)=(c1[c5](c3[c5]c4)))
  ⊣ c2 =(Base ` c0)
  ⊣ c5 =(meet ` c0)
} = {
  sylan(c0 ∈ Lat, (c1 ∈ c2 ∧ c3 ∈ c2 ∧ c4 ∈ c2), ((c1[c5]c3)[c5]c4)=(c1[c5](c3[c5]c4)), (ODual ` c0)∈ Lat)
  odulat(c0, (ODual ` c0))
  latjass((ODual ` c0), c1, c2, c3, c4, c5)
  odubas(c2, (ODual ` c0), c0)
  odujoin(c5, (ODual ` c0), c0)
  eqid((ODual ` c0))
}
