thm islpir2(class c0, class c1, class c2) {
  |- wb(wcel(c0, clpir), wa(wcel(c0, crg), wss(c1, c2)))
  -| wceq(c2, cfv(clpidl, c0))
  -| wceq(c1, cfv(clidl, c0))
} = {
  bitri(wcel(c0, clpir), wa(wcel(c0, crg), wss(c1, c2)), wa(wcel(c0, crg), wceq(c1, c2)))
  islpir(c0, c1, c2)
  pm5.32i(wcel(c0, crg), wceq(c1, c2), wss(c1, c2))
  syl6rbbr(wcel(c0, crg), wceq(c1, c2), wss(c1, c2), wa(wss(c1, c2), wss(c2, c1)))
  biantrud(wcel(c0, crg), wss(c1, c2), wss(c2, c1))
  lpiss(c0, c2, c1)
  eqss(c1, c2)
}

blocks.pretty:
thm islpir2(class c0, class c1, class c2) {
  ⊢ (c0 ∈ LPIR ↔(c0 ∈ Ring ∧ c1 ⊆ c2))
  ⊣ c2 =(LPIdeal ` c0)
  ⊣ c1 =(LIdeal ` c0)
} = {
  bitri(c0 ∈ LPIR, (c0 ∈ Ring ∧ c1 ⊆ c2), (c0 ∈ Ring ∧ c1 = c2))
  islpir(c0, c1, c2)
  pm5.32i(c0 ∈ Ring, c1 = c2, c1 ⊆ c2)
  syl6rbbr(c0 ∈ Ring, c1 = c2, c1 ⊆ c2, (c1 ⊆ c2 ∧ c2 ⊆ c1))
  biantrud(c0 ∈ Ring, c1 ⊆ c2, c2 ⊆ c1)
  lpiss(c0, c2, c1)
  eqss(c1, c2)
}

