thm indisconn(class c0) {
  ⊢ { ∅, c0 } ∈ Conn
} = {
  mpbir2an({ ∅, c0 } ∈ Conn, { ∅, c0 } ∈ Top, ({ ∅, c0 } ⋂(Clsd ` { ∅, c0 }))⊆ { ∅,(= ` c0)})
  indistop(c0)
  sseqtr4i(({ ∅, c0 } ⋂(Clsd ` { ∅, c0 })), { ∅,(= ` c0)}, { ∅, c0 })
  inss1({ ∅, c0 }, (Clsd ` { ∅, c0 }))
  indislem(c0)
  isconn2({ ∅, c0 }, (= ` c0))
  indisuni(c0)
}
