axiom df-1stc(setvar s0, setvar s1, setvar s2, setvar s3) {
  ⊢ 1stc = { s0 ∈ Top | ∀(s1 ∈ ⋃ s0, ∃(s2 ∈ P(s0),(s2[≼]_om ∧ ∀(s3 ∈ s0,(s1 ∈ s3 → s1 ∈ ⋃(s2 ⋂ P(s3)))))))}
  ⊣ d(s0, s1)
  ⊣ d(s0, s2)
  ⊣ d(s0, s3)
  ⊣ d(s1, s2)
  ⊣ d(s1, s3)
  ⊣ d(s2, s3)
}

