  rexlimiva(hs0, ctb, wa(wbr(cv(hs0), cdom, com), wceq(cfv(ctg, cv(hs0)), c0)), wcel(c0, ctop))
  eqeltrrd(c0, ctop, cfv(ctg, cv(hs0)), wa(wcel(cv(hs0), ctb), wa(wbr(cv(hs0), cdom, com), wceq(cfv(ctg, cv(hs0)), c0))))
