thm ditgeq3dv(setvar s0, class c0, class c1, class c2, class c3, wff w0) {
  ⊢ (w0 → S_[c0 → c1](c2, s0)= S_[c0 → c1](c3, s0))
  ⊣ ((w0 ∧ s0 ∈ ℝ)→ c2 = c3)
  ⊣ d(s0, c0)
  ⊣ d(s0, c1)
  ⊣ d(s0, w0)
} = {
  syl(w0, S_[c0 → c1](c2, s0)= S_[c0 → c1](c3, s0), ∀(s0 ∈ ℝ, c2 = c3))
  ralrimiva(s0, cr, w0, c2 = c3)
  ditgeq3(s0, c2, c3, c0, c1)
}

