  3adant3(wcel(c0, chba), wcel(c1, chba), wbr(cfv(cno, c1), cle, nat1), wceq(cfv(cabs, co(c0, csp, c1)), cfv(cabs, co(c1, csp, c0))))
  abshicom(c0, c1)
  3com12(wcel(c0, chba), wcel(c1, chba), wbr(cfv(cno, c1), cle, nat1), wbr(cfv(cabs, co(c1, csp, c0)), cle, cfv(cno, c0)))
