} = {
  eqeltrd((c0[+H](span ` { c1 })), cch, (c0[vH](span ` { c1 })), (c0 ∈ CH ∧ c1 ∈ ~H))
  spansnj(c0, c1)
  sylan2(c0 ∈ CH, c1 ∈ ~H, (c0[vH](span ` { c1 }))∈ CH, (span ` { c1 })∈ CH)
  spansnch(c1)
  chjcl(c0, (span ` { c1 }))
}
