  |- wceq(cnop, cmpt(s0, co(chba, cmap, chba), csup(cab(s1, wrex(s2, chba, wa(wbr(cfv(cno, cv(s2)), cle, nat1), wceq(cv(s1), cfv(cno, cfv(cv(s0), cv(s2))))))), cxr, clt)))
  -| diffss(s0, s1)
