  -| diffss(s1, s2)
}

blocks.pretty:
axiom df-nmop(setvar s0, setvar s1, setvar s2) {
  ⊢ normop =(s0 ∈(~H[↦]~H)↦ sup({ s1 | ∃(s2 ∈ ~H,((normh ` s2)[≤]1 ∧ s1 =(normh `(s0 ` s2))))}, ℝ*, <))
  ⊣ d(s0, s1)
  ⊣ d(s0, s2)
  ⊣ d(s1, s2)
}

axiom df-lnop(setvar s0, setvar s1, setvar s2, setvar s3) {
  ⊢ LinOp = { s0 ∈(~H[↦]~H)| ∀(s1 ∈ ℂ, ∀(s2 ∈ ~H, ∀(s3 ∈ ~H,(s0 `((s1[.h]s2)[+h]s3))=((s1[.h](s0 ` s2))[+h](s0 ` s3)))))}
  ⊣ d(s0, s1)
  ⊣ d(s0, s2)
  ⊣ d(s0, s3)
  ⊣ d(s1, s2)
  ⊣ d(s1, s3)
  ⊣ d(s2, s3)
}

