axiom df-nmfn(setvar s0, setvar s1, setvar s2) {
  |- wceq(cnmf, cmpt(s0, co(cc, cmap, chba), csup(cab(s1, wrex(s2, chba, wa(wbr(cfv(cno, cv(s2)), cle, nat1), wceq(cv(s1), cfv(cabs, cfv(cv(s0), cv(s2))))))), cxr, clt)))
  -| diffss(s0, s1)
  -| diffss(s0, s2)
  -| diffss(s1, s2)
}

